  .card-shadow {
    box-shadow: 
      0 1px 3px 0 rgb(0 0 0 / 0.1),
      0 1px 2px -1px rgb(0 0 0 / 0.1);
  }

  .card-shadow-lg {
    box-shadow: 
      0 10px 15px -3px rgb(0 0 0 / 0.1),
      0 4px 6px -4px rgb(0 0 0 / 0.1);
  }

  /* Text clamp utilities */
  .line-clamp-1 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 1;
  }

  .line-clamp-2 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 2;
  }

  .line-clamp-3 {
    overflow: hidden;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    -webkit-line-clamp: 3;
  }

  /* Drag and drop visual feedback */
  .drag-over {
    @apply bg-primary/5 border-primary/30 border-dashed;
  }

  .dragging {
    @apply opacity-50 scale-95 rotate-2;
  }

  /* Animation improvements */
  .animate-in {
    animation: animateIn 0.2s ease-out;
  }

  .animate-out {
    animation: animateOut 0.15s ease-in;
  }

  @keyframes animateIn {
    from {
      opacity: 0;
      transform: translateY(-4px) scale(0.98);
    }
    to {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
  }

  @keyframes animateOut {
    from {
      opacity: 1;
      transform: translateY(0) scale(1);
    }
    to {
      opacity: 0;
      transform: translateY(-4px) scale(0.98);
    }
  }

  /* Glass morphism effect for modals and floating elements */
  .glass {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  .dark .glass {
    background: rgba(0, 0, 0, 0.7);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  /* Improved spacing consistency */
  .content-spacing > * + * {
    @apply mt-4;
  }

  .content-spacing-lg > * + * {
    @apply mt-6;
  }

  .content-spacing-sm > * + * {
    @apply mt-2;
  }
}

@layer components {
  /* Button variants with consistent spacing and typography */
  .btn-primary {
    @apply bg-primary text-primary-foreground hover:bg-primary/90 focus:ring-primary;
  }

  .btn-secondary {
    @apply bg-secondary text-secondary-foreground hover:bg-secondary/90 focus:ring-secondary;
  }

  .btn-accent {
    @apply bg-accent text-accent-foreground hover:bg-accent/90 focus:ring-accent;
  }

  /* Form field consistency */
  .form-field {
    @apply space-y-2;
  }

  .form-label {
    @apply text-sm font-medium text-foreground;
  }

  .form-input {
    @apply w-full px-3 py-2 border border-input bg-background text-foreground rounded-md focus:ring-2 focus:ring-ring focus:border-transparent;
  }

  /* Layout utilities */
  .container-padding {
    @apply px-4 sm:px-6 lg:px-8;
  }

  .section-padding {
    @apply py-12 md:py-16 lg:py-20;
  }

  /* Text utilities matching design system */
  .text-hero {
    @apply text-4xl md:text-5xl lg:text-6xl font-bold tracking-tight;
  }

  .text-heading {
    @apply text-2xl md:text-3xl font-semibold tracking-tight;
  }

  .text-subheading {
    @apply text-lg md:text-xl font-medium;
  }

  .text-body {
    @apply text-base leading-relaxed;
  }

  .text-caption {
    @apply text-sm text-muted-foreground;
  }

  /* Card components */
  .card-base {
    @apply bg-card text-card-foreground border border-border rounded-lg;
  }

  .card-hover {
    @apply hover:shadow-lg transition-shadow duration-200;
  }

  .card-interactive {
    @apply cursor-pointer hover:bg-accent/5 transition-colors duration-200;
  }
}

@layer utilities {
  /* Spacing utilities matching design requirements */
  .space-consistent > * + * {
    @apply mt-4;
  }

  .gap-consistent {
    @apply gap-4;
  }

  .gap-consistent-lg {
    @apply gap-6;
  }

  /* Border radius consistency */
  .rounded-consistent {
    @apply rounded-lg;
  }

  .rounded-consistent-sm {
    @apply rounded-md;
  }

  .rounded-consistent-lg {
    @apply rounded-xl;
  }

  /* Shadow utilities */
  .shadow-consistent {
    @apply shadow-sm;
  }

  .shadow-consistent-lg {
    @apply shadow-lg;
  }
}

