import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Bot, Send, Mic, Lightbulb } from "lucide-react";

interface AiChatProps {
  workspaceId: number | null;
}

export default function AiChat({ workspaceId }: AiChatProps) {
  const [message, setMessage] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch chat history
  const { data: conversations = [] } = useQuery({
    queryKey: [`/api/workspaces/${workspaceId}/chat`],
    enabled: !!workspaceId,
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (messageText: string) => {
      if (!workspaceId) throw new Error("No workspace selected");
      return await apiRequest("POST", `/api/workspaces/${workspaceId}/chat`, {
        message: messageText,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/workspaces/${workspaceId}/chat`] });
      setMessage("");
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!message.trim() || sendMessageMutation.isPending) return;
    sendMessageMutation.mutate(message.trim());
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const suggestedActions = [
    "ðŸ“Š Show project overview",
    "âš¡ Create quick task", 
    "ðŸ“… Schedule meeting",
    "ðŸ’¡ Get insights",
  ];

  return (
    <Card className="bg-white rounded-2xl shadow-sm border border-gray-200">
      <CardHeader>
        <div className="flex items-center">
          <div className="w-8 h-8 bg-gradient-to-r from-primary to-secondary rounded-full flex items-center justify-center text-white mr-3">
            <Bot className="h-4 w-4" />
          </div>
          <CardTitle className="text-lg font-semibold text-gray-900">AI Assistant</CardTitle>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Chat Input Area */}
        <div className="relative">
          <Textarea
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask or find anything from your workspace... Try 'Show me overdue tasks' or 'Create a new project for client onboarding'"
            className="resize-none focus:ring-2 focus:ring-primary focus:border-transparent pr-20"
            rows={3}
            disabled={sendMessageMutation.isPending}
          />
          <div className="absolute bottom-3 right-3 flex space-x-2">
            <Button variant="ghost" size="sm" disabled>
              <Mic className="h-4 w-4" />
            </Button>
            <Button 
              size="sm" 
              onClick={handleSendMessage}
              disabled={!message.trim() || sendMessageMutation.isPending}
            >
              {sendMessageMutation.isPending ? (
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
              ) : (
                <Send className="h-4 w-4" />
              )}
            </Button>
          </div>
        </div>

        {/* Suggested Actions */}
        <div className="flex flex-wrap gap-2">
          {suggestedActions.map((action, index) => (
            <Badge 
              key={index}
              variant="secondary"
              className="cursor-pointer hover:bg-gray-200 transition-colors"
              onClick={() => setMessage(action.replace(/^\S+\s/, ''))}
            >
              {action}
            </Badge>
          ))}
        </div>

        {/* Chat History */}
        <div className="border-t border-gray-100 pt-4">
          <div className="text-sm text-gray-500 mb-2">Recent conversation:</div>
          <ScrollArea className="h-48">
            <div className="space-y-3">
              {conversations.length === 0 ? (
                <div className="text-center py-4 text-gray-400">
                  <Lightbulb className="h-8 w-8 mx-auto mb-2 opacity-50" />
                  <p className="text-sm">Start a conversation with your AI assistant</p>
                </div>
              ) : (
                conversations
                  .slice()
                  .reverse()
                  .map((conv: any) => (
                    <div key={conv.id} className="space-y-2">
                      <div className="p-3 bg-gray-50 rounded-lg text-sm">
                        <strong>You:</strong> {conv.message}
                      </div>
                      {conv.response && (
                        <div className="p-3 bg-primary/5 rounded-lg text-sm">
                          <strong>AI:</strong> {conv.response}
                        </div>
                      )}
                    </div>
                  ))
              )}
            </div>
          </ScrollArea>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/brain-dump.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { GripVertical, X, Plus, Lightbulb } from "lucide-react";

interface BrainDumpProps {
  workspaceId: number | null;
}

export default function BrainDump({ workspaceId }: BrainDumpProps) {
  const [newItem, setNewItem] = useState("");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch brain dump items
  const { data: brainDumpItems = [], isLoading } = useQuery({
    queryKey: [`/api/workspaces/${workspaceId}/brain-dump`],
    enabled: !!workspaceId,
  });

  // Add brain dump item mutation
  const addItemMutation = useMutation({
    mutationFn: async (text: string) => {
      if (!workspaceId) throw new Error("No workspace selected");
      return await apiRequest("POST", `/api/workspaces/${workspaceId}/brain-dump`, {
        text,
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/workspaces/${workspaceId}/brain-dump`] });
      setNewItem("");
      toast({
        title: "Item added",
        description: "Brain dump item has been added successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to add brain dump item.",
        variant: "destructive",
      });
    },
  });

  // Remove brain dump item mutation
  const removeItemMutation = useMutation({
    mutationFn: async (itemId: number) => {
      return await apiRequest("DELETE", `/api/brain-dump/${itemId}`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/workspaces/${workspaceId}/brain-dump`] });
      toast({
        title: "Item removed",
        description: "Brain dump item has been removed successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to remove brain dump item.",
        variant: "destructive",
      });
    },
  });

  // Clear all brain dump items mutation
  const clearAllMutation = useMutation({
    mutationFn: async () => {
      if (!workspaceId) throw new Error("No workspace selected");
      return await apiRequest("DELETE", `/api/workspaces/${workspaceId}/brain-dump`, {});
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/workspaces/${workspaceId}/brain-dump`] });
      toast({
        title: "Brain dump cleared",
        description: "All brain dump items have been removed.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to clear brain dump.",
        variant: "destructive",
      });
    },
  });

  const handleAddItem = () => {
    if (!newItem.trim() || addItemMutation.isPending) return;
    addItemMutation.mutate(newItem.trim());
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault();
      handleAddItem();
    }
  };

  const handleRemoveItem = (itemId: number) => {
    removeItemMutation.mutate(itemId);
  };

  const handleClearAll = () => {
    if (brainDumpItems.length === 0) return;
    clearAllMutation.mutate();
  };

  const handleDragStart = (e: React.DragEvent, item: any) => {
    e.dataTransfer.setData("application/json", JSON.stringify({
      type: "brain-dump-item",
      data: item,
    }));
  };

  return (
    <div>
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-gray-900">Brain Dump</h3>
        <Button 
          variant="ghost" 
          size="sm" 
          onClick={handleClearAll}
          disabled={brainDumpItems.length === 0 || clearAllMutation.isPending}
          className="text-sm text-gray-500 hover:text-gray-700"
        >
          Clear
        </Button>
      </div>

      {/* Add new item input */}
      <div className="mb-4">
        <div className="flex">
          <Input
            type="text"
            placeholder="Quick thoughts, ideas, or tasks..."
            value={newItem}
            onChange={(e) => setNewItem(e.target.value)}
            onKeyPress={handleKeyPress}
            className="flex-1 text-sm focus:ring-2 focus:ring-primary focus:border-transparent"
            disabled={addItemMutation.isPending}
          />
          <Button 
            className="ml-2 px-3 py-2"
            onClick={handleAddItem}
            disabled={!newItem.trim() || addItemMutation.isPending}
          >
            {addItemMutation.isPending ? (
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
            ) : (
              <Plus className="h-4 w-4" />
            )}
          </Button>
        </div>
      </div>

      {/* Brain dump items list */}
      <div className="space-y-2">
        {isLoading ? (
          <div className="space-y-2">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-10 bg-gray-100 rounded-lg animate-pulse"></div>
            ))}
          </div>
        ) : brainDumpItems.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <Lightbulb className="h-8 w-8 mx-auto mb-2 opacity-50" />
            <p className="text-sm">No ideas captured yet</p>
            <p className="text-xs text-gray-400 mt-1">
              Add quick thoughts and drag them to time slots
            </p>
          </div>
        ) : (
          brainDumpItems.map((item: any) => (
            <div
              key={item.id}
              className="flex items-center p-2 bg-gray-50 rounded-lg border border-gray-200 cursor-move hover:bg-gray-100 group transition-colors"
              draggable
              onDragStart={(e) => handleDragStart(e, item)}
            >
              <GripVertical className="h-4 w-4 text-gray-400 mr-2 group-hover:text-gray-600" />
              <span className="flex-1 text-sm text-gray-700">{item.text}</span>
              <Button
                variant="ghost"
                size="sm"
                className="opacity-0 group-hover:opacity-100 p-1 h-auto text-gray-400 hover:text-red-500"
                onClick={() => handleRemoveItem(item.id)}
                disabled={removeItemMutation.isPending}
              >
                <X className="h-3 w-3" />
              </Button>
            </div>
          ))
        )}
      </div>

      {/* Instruction tip */}
      {brainDumpItems.length > 0 && (
        <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg">
          <div className="flex items-center text-sm text-blue-800">
            <Lightbulb className="h-4 w-4 mr-2" />
            <span>Drag items to time slots to convert to tasks</span>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/global-search.tsx.content:
import { useState, useRef, useEffect } from 'react';
import { Search, X, Calendar, Users, Tag, FolderOpen } from 'lucide-react';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useQuery } from '@tanstack/react-query';

interface GlobalSearchProps {
  workspaceId: number | null;
  onTaskSelect?: (taskId: number) => void;
  onProjectSelect?: (projectId: number) => void;
  onMemberSelect?: (memberId: number) => void;
  onCategorySelect?: (categoryId: number) => void;
}

interface SearchResults {
  tasks: any[];
  projects: any[];
  members: any[];
  categories: any[];
}

export default function GlobalSearch({
  workspaceId,
  onTaskSelect,
  onProjectSelect,
  onMemberSelect,
  onCategorySelect
}: GlobalSearchProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(-1);
  const searchRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Fetch search results when query changes
  const { data: searchResults, isLoading } = useQuery<SearchResults>({
    queryKey: ['/api/search', workspaceId, searchQuery],
    queryFn: async () => {
      const response = await fetch(`/api/search?workspaceId=${workspaceId}&q=${encodeURIComponent(searchQuery)}`);
      if (!response.ok) throw new Error('Search failed');
      return response.json();
    },
    enabled: !!workspaceId && searchQuery.length >= 2 && isOpen,
    staleTime: 1000 * 30, // 30 seconds
  });

  // Close search when clicking outside
  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (searchRef.current && !searchRef.current.contains(event.target as Node)) {
        setIsOpen(false);
        setSelectedIndex(-1);
      }
    }

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Handle keyboard navigation
  useEffect(() => {
    function handleKeyDown(event: KeyboardEvent) {
      if (!isOpen || !searchResults) return;

      const allResults = [
        ...searchResults.tasks,
        ...searchResults.projects,
        ...searchResults.members,
        ...searchResults.categories
      ];

      switch (event.key) {
        case 'ArrowDown':
          event.preventDefault();
          setSelectedIndex(prev => (prev + 1) % allResults.length);
          break;
        case 'ArrowUp':
          event.preventDefault();
          setSelectedIndex(prev => prev <= 0 ? allResults.length - 1 : prev - 1);
          break;
        case 'Enter':
          event.preventDefault();
          if (selectedIndex >= 0) {
            handleResultSelect(allResults[selectedIndex]);
          }
          break;
        case 'Escape':
          setIsOpen(false);
          setSelectedIndex(-1);
          inputRef.current?.blur();
          break;
      }
    }

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, searchResults, selectedIndex]);

  const handleResultSelect = (result: any) => {
    if (result.title) {
      // It's a task
      onTaskSelect?.(result.id);
    } else if (result.name && result.description !== undefined) {
      // It's a project
      onProjectSelect?.(result.id);
    } else if (result.name && result.email) {
      // It's a member
      onMemberSelect?.(result.id);
    } else if (result.name && result.color !== undefined) {
      // It's a category
      onCategorySelect?.(result.id);
    }

    setIsOpen(false);
    setSearchQuery('');
    setSelectedIndex(-1);
  };

  const clearSearch = () => {
    setSearchQuery('');
    setIsOpen(false);
    setSelectedIndex(-1);
    inputRef.current?.focus();
  };

  const getResultIndex = (sectionResults: any[], sectionStartIndex: number, itemIndex: number) => {
    return sectionStartIndex + itemIndex;
  };

  const renderTaskResult = (task: any, index: number, globalIndex: number) => (
    <div
      key={`task-${task.id}`}
      className={`flex items-center justify-between p-3 cursor-pointer transition-colors ${
        selectedIndex === globalIndex ? 'bg-primary/10 border-l-2 border-primary' : 'hover:bg-gray-50'
      }`}
      onClick={() => handleResultSelect(task)}
    >
      <div className="flex-1 min-w-0">
        <div className="flex items-center space-x-2">
          <h4 className="text-sm font-medium text-gray-900 truncate">{task.title}</h4>
          <Badge variant={task.status === 'completed' ? 'default' : 'secondary'} className="text-xs">
            {task.status}
          </Badge>
        </div>
        {task.description && (
          <p className="text-xs text-gray-500 mt-1 truncate">{task.description}</p>
        )}
        <div className="flex items-center space-x-3 mt-2 text-xs text-gray-400">
          {task.dueDate && (
            <span className="flex items-center">
              <Calendar className="h-3 w-3 mr-1" />
              {new Date(task.dueDate).toLocaleDateString()}
            </span>
          )}
          {task.projectName && (
            <span className="flex items-center">
              <FolderOpen className="h-3 w-3 mr-1" />
              {task.projectName}
            </span>
          )}
        </div>
      </div>
    </div>
  );

  const renderProjectResult = (project: any, index: number, globalIndex: number) => (
    <div
      key={`project-${project.id}`}
      className={`flex items-center justify-between p-3 cursor-pointer transition-colors ${
        selectedIndex === globalIndex ? 'bg-primary/10 border-l-2 border-primary' : 'hover:bg-gray-50'
      }`}
      onClick={() => handleResultSelect(project)}
    >
      <div className="flex-1 min-w-0">
        <div className="flex items-center space-x-2">
          <FolderOpen className="h-4 w-4 text-blue-500" />
          <h4 className="text-sm font-medium text-gray-900 truncate">{project.name}</h4>
        </div>
        {project.description && (
          <p className="text-xs text-gray-500 mt-1 truncate">{project.description}</p>
        )}
      </div>
    </div>
  );

  const renderMemberResult = (member: any, index: number, globalIndex: number) => {
    const memberName = member.name || member.email;

    return (
      <div
        key={`member-${member.id}`}
        className={`flex items-center justify-between p-3 cursor-pointer transition-colors ${
          selectedIndex === globalIndex ? 'bg-primary/10 border-l-2 border-primary' : 'hover:bg-gray-50'
        }`}
        onClick={() => handleResultSelect(member)}
      >
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-gradient-to-br from-blue-500 to-purple-600 rounded-full flex items-center justify-center">
            <span className="text-white text-xs font-semibold">
              {memberName.charAt(0).toUpperCase()}
            </span>
          </div>
          <div>
            <h4 className="text-sm font-medium text-gray-900">{memberName}</h4>
            {member.email && (
              <p className="text-xs text-gray-500">{member.email}</p>
            )}
          </div>
        </div>
      </div>
    );
  };

  const renderCategoryResult = (category: any, index: number, globalIndex: number) => (
    <div
      key={`category-${category.id}`}
      className={`flex items-center justify-between p-3 cursor-pointer transition-colors ${
        selectedIndex === globalIndex ? 'bg-primary/10 border-l-2 border-primary' : 'hover:bg-gray-50'
      }`}
      onClick={() => handleResultSelect(category)}
    >
      <div className="flex items-center space-x-3">
        <Tag className="h-4 w-4 text-green-500" />
        <h4 className="text-sm font-medium text-gray-900">{category.name}</h4>
      </div>
    </div>
  );

  return (
    <div ref={searchRef} className="relative">
      <div className="relative">
        <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
        <Input
          ref={inputRef}
          type="text"
          placeholder="Search tasks, projects, members..."
          value={searchQuery}
          onChange={(e) => {
            setSearchQuery(e.target.value);
            setIsOpen(e.target.value.length >= 2);
            setSelectedIndex(-1);
          }}
          onFocus={() => {
            if (searchQuery.length >= 2) {
              setIsOpen(true);
            }
          }}
          className="pl-10 pr-10 w-64 md:w-80"
        />
        {searchQuery && (
          <Button
            variant="ghost"
            size="sm"
            onClick={clearSearch}
            className="absolute right-1 top-1/2 transform -translate-y-1/2 h-6 w-6 p-0 hover:bg-gray-100"
          >
            <X className="h-3 w-3" />
          </Button>
        )}
      </div>

      {/* Search Results Dropdown */}
      {isOpen && searchQuery.length >= 2 && (
        <Card className="absolute top-full left-0 mt-1 max-h-[600px] overflow-y-auto z-50 border shadow-lg w-[600px]">
          <CardContent className="p-0">
            {isLoading ? (
              <div className="p-4 text-center text-gray-500">
                <div className="flex items-center justify-center space-x-2">
                  <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin"></div>
                  <span>Searching...</span>
                </div>
              </div>
            ) : searchResults ? (
              <div className="grid grid-cols-2 gap-0">
                {/* Tasks Section */}
                {searchResults.tasks && searchResults.tasks.length > 0 && (
                  <div className="border-r border-gray-100">
                    <div className="px-3 py-2 bg-gray-50 border-b">
                      <div className="flex items-center space-x-2">
                        <Calendar className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-semibold text-gray-700">
                          Tasks ({searchResults.tasks.length})
                        </h3>
                      </div>
                    </div>
                    <div className="max-h-80 overflow-y-auto">
                      {searchResults.tasks.slice(0, 6).map((task, index) => 
                        renderTaskResult(task, index, getResultIndex([], 0, index))
                      )}
                      {searchResults.tasks.length > 6 && (
                        <div className="px-3 py-1 text-xs text-gray-400 bg-gray-25">
                          +{searchResults.tasks.length - 6} more tasks
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Projects Section */}
                {searchResults.projects && searchResults.projects.length > 0 && (
                  <div className={searchResults.tasks?.length ? "" : "border-r border-gray-100"}>
                    <div className="px-3 py-2 bg-gray-50 border-b">
                      <div className="flex items-center space-x-2">
                        <FolderOpen className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-semibold text-gray-700">
                          Projects ({searchResults.projects.length})
                        </h3>
                      </div>
                    </div>
                    <div className="max-h-80 overflow-y-auto">
                      {searchResults.projects.slice(0, 6).map((project, index) => 
                        renderProjectResult(project, index, getResultIndex(searchResults.tasks, searchResults.tasks?.length || 0, index))
                      )}
                      {searchResults.projects.length > 6 && (
                        <div className="px-3 py-1 text-xs text-gray-400 bg-gray-25">
                          +{searchResults.projects.length - 6} more projects
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Members Section */}
                {searchResults.members && searchResults.members.length > 0 && (
                  <div className={`${(searchResults.tasks?.length || searchResults.projects?.length) ? "border-t border-gray-100" : ""} border-r border-gray-100`}>
                    <div className="px-3 py-2 bg-gray-50 border-b">
                      <div className="flex items-center space-x-2">
                        <Users className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-semibold text-gray-700">
                          Members ({searchResults.members.length})
                        </h3>
                      </div>
                    </div>
                    <div className="max-h-80 overflow-y-auto">
                      {searchResults.members.slice(0, 6).map((member, index) => 
                        renderMemberResult(member, index, getResultIndex(
                          [...(searchResults.tasks || []), ...(searchResults.projects || [])], 
                          (searchResults.tasks?.length || 0) + (searchResults.projects?.length || 0), 
                          index
                        ))
                      )}
                      {searchResults.members.length > 6 && (
                        <div className="px-3 py-1 text-xs text-gray-400 bg-gray-25">
                          +{searchResults.members.length - 6} more members
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* Categories Section */}
                {searchResults.categories && searchResults.categories.length > 0 && (
                  <div className={(searchResults.tasks?.length || searchResults.projects?.length) ? "border-t border-gray-100" : ""}>
                    <div className="px-3 py-2 bg-gray-50 border-b">
                      <div className="flex items-center space-x-2">
                        <Tag className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-semibold text-gray-700">
                          Categories ({searchResults.categories.length})
                        </h3>
                      </div>
                    </div>
                    <div className="max-h-80 overflow-y-auto">
                      {searchResults.categories.slice(0, 6).map((category, index) => 
                        renderCategoryResult(category, index, getResultIndex(
                          [...(searchResults.tasks || []), ...(searchResults.projects || []), ...(searchResults.members || [])], 
                          (searchResults.tasks?.length || 0) + (searchResults.projects?.length || 0) + (searchResults.members?.length || 0), 
                          index
                        ))
                      )}
                      {searchResults.categories.length > 6 && (
                        <div className="px-3 py-1 text-xs text-gray-400 bg-gray-25">
                          +{searchResults.categories.length - 6} more categories
                        </div>
                      )}
                    </div>
                  </div>
                )}

                {/* No Results */}
                {(!searchResults.tasks?.length && !searchResults.projects?.length && 
                  !searchResults.members?.length && !searchResults.categories?.length) && (
                  <div className="col-span-2 p-4 text-center text-gray-500">
                    <Search className="h-8 w-8 mx-auto mb-2 text-gray-300" />
                    <p className="text-sm">No results found for "{searchQuery}"</p>
                    <p className="text-xs text-gray-400 mt-1">Try a different search term</p>
                  </div>
                )}
              </div>
            ) : null}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

