                          variant="ghost"
                          size="sm"
                          className={`w-full justify-start text-xs hover:bg-gray-100 ${
                            isVaultActive &&
                            activeProjectId === project.id.toString()
                              ? "bg-blue-50 text-blue-700 border-l-2 border-blue-500"
                              : "text-gray-600"
                          }`}
                        >
                          <Archive className="h-3 w-3 mr-2" />
                          Vault
