                  </CardHeader>
                  <CardContent>
                    <div className={`text-2xl font-bold ${netProfit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                      ${netProfit.toFixed(2)}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {netProfit >= 0 ? 'Profit' : 'Loss'}
                    </div>
                  </CardContent>
                </Card>
              </div>

              <Card>
