                    />
                    <div className="text-sm text-muted-foreground">
                      {budgetUtilization > 100 ? 'Over budget' : 
                       budgetUtilization > 80 ? 'Approaching budget limit' : 
                       'Within budget'}
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Pie Chart Summaries */}
              <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                {/* Budget Distribution by Component */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <Calculator className="h-4 w-4" />
                      <span>Budget by Component</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {budgetChartData.length > 0 ? (
                      <ResponsiveContainer width="100%" height={200}>
                        <RechartsPieChart>
                          <Pie
                            data={budgetChartData}
                            cx="50%"
                            cy="50%"
                            innerRadius={40}
                            outerRadius={80}
                            paddingAngle={5}
                            dataKey="value"
                          >
                            {budgetChartData.map((entry, index) => (
                              <Cell key={`cell-${index}`} fill={entry.fill} />
                            ))}
                          </Pie>
                          <Tooltip formatter={(value) => [`$${parseFloat(value as string).toFixed(2)}`, 'Budget']} />
                          <Legend />
                        </RechartsPieChart>
                      </ResponsiveContainer>
                    ) : (
                      <div className="h-48 flex items-center justify-center text-gray-500">
                        <div className="text-center">
                          <PieChart className="h-12 w-12 mx-auto mb-2 text-gray-300" />
                          <p>No budget data</p>
                        </div>
                      </div>
                    )}
                  </CardContent>
                </Card>

                {/* Expenses Distribution by Component */}
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <TrendingDown className="h-4 w-4" />
                      <span>Expenses by Component</span>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    {expenseChartData.length > 0 ? (
                      <ResponsiveContainer width="100%" height={200}>
                        <RechartsPieChart>
                          <Pie
